import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

function Login() {
  const [emailPrefix, setEmailPrefix] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const emailDomain = "@psn.co.id";
  const fullEmail = emailPrefix ? `${emailPrefix}${emailDomain}` : "";

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    try {
      const response = await axios.post("http://192.168.1.13:4000/login", { email: fullEmail, password });
      localStorage.setItem("email", fullEmail);
      localStorage.setItem("password", password);
      navigate("/Daily-Activity");
    } catch (err) {
      setError("Login failed. Check your credentials.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100" style={{ backgroundColor: "#edf0f2" }}>
      <div className="card p-4 shadow-lg" style={{ width: "420px", borderRadius: "15px", backgroundColor: "#ffffff" }}>
        <div className="text-center mb-1">
          <img src="/logonmc1.png" alt="Logo" style={{ width: "200px", borderRadius: "10px", marginBottom: "-5px" }} />
        </div>
        <h2 className="text-center" style={{ color: "#37474f", fontWeight: "bold" }}>Welcome Back NMC Team</h2>
        <p className="text-center text-muted">Please log in to continue</p>
        <form onSubmit={handleLogin}>
          <div className="mb-3">
            <label className="form-label">Email</label>
            <div className="input-group">
              <input
                type="text"
                className="form-control"
                placeholder="Your Email Prefix (e.g., johndoe)"
                value={emailPrefix}
                onChange={(e) => setEmailPrefix(e.target.value)}
                required
              />
              <span className="input-group-text">{emailDomain}</span>
            </div>
          </div>
          <div className="mb-3">
            <label className="form-label">Password</label>
            <input
              type="password"
              className="form-control"
              placeholder="Your Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          {error && <p className="text-danger text-center">{error}</p>}
          <button type="submit" className="btn btn-primary w-100" disabled={loading}>
            {loading ? "Logging in..." : "Login"}
          </button>
          <div className="text-center mt-3">
            <a href="https://cboss.psn.co.id/" target="_blank" rel="noopener noreferrer" className="btn btn-link text-secondary">
              Login to CBOSS
            </a>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Login;
