import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { FiPower } from "react-icons/fi"; // Import icon power dari react-icons
import "bootstrap/dist/css/bootstrap.min.css";
import "./SendEmail.css";

function SendEmail() {
  const [subject, setSubject] = useState("");
  const [message, setMessage] = useState("");
  const [status, setStatus] = useState({ type: "", text: "" });
  const navigate = useNavigate();
  const recipientEmail = "group.nmc@psn.co.id";

  useEffect(() => {
    if (!localStorage.getItem("email") || !localStorage.getItem("password")) {
      navigate("/");
    }
  }, [navigate]);

  const handleSendEmail = async (e) => {
    e.preventDefault();
    try {
      const email = localStorage.getItem("email");
      const password = localStorage.getItem("password");

      await axios.post("http://192.168.1.13:4000/send-email", {
        email,
        password,
        to: recipientEmail,
        subject,
        message,
      });

      setSubject("");
      setMessage("");
      setStatus({ type: "success", text: "✅ Email sent successfully!" });
      setTimeout(() => setStatus({ type: "", text: "" }), 3000);
    } catch (err) {
      setStatus({ type: "error", text: "❌ Failed to send email. Please try again." });
      console.error("Email sending error:", err);
    }
  };

  return (
    <div className="email-card">
      {/* Tombol Logout di Pojok Kanan */}
      <motion.button
        className="btn-logout"
        onClick={() => navigate("/logout")}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
      >
        <FiPower size={24} />
      </motion.button>

      <motion.img
        src="/logonmc1.png"
        alt="Logo"
        className="logo"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      />
      <h2 className="title">Halo NMC Team!</h2>
      <p className="subtitle">Silakan submit daily activity kamu sebelum pulang</p>

      {status.text && (
        <motion.div
          className={`alert ${status.type === "success" ? "alert-success" : "alert-danger"} text-center`}
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          {status.text}
        </motion.div>
      )}

      <form onSubmit={handleSendEmail} className="email-form">
        <div className="mb-3">
          <label className="form-label">Recipient Email</label>
          <input type="email" className="form-control" value={recipientEmail} readOnly />
        </div>
        <div className="mb-3">
          <label className="form-label">Subject</label>
          <input
            type="text"
            className="form-control"
            placeholder="Enter email subject"
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Detail</label>
          <textarea
            className="form-control"
            placeholder="Input your daily activity"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            required
            rows="5"
          ></textarea>
        </div>

        {/* Tombol Submit di Bawah */}
        <motion.button
          type="submit"
          className="btn btn-submit"
          disabled={!subject || !message}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          Submit
        </motion.button>
      </form>
    </div>
  );
}

export default SendEmail;
