import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { motion } from "framer-motion";

function SendEmail() {
  const [subject, setSubject] = useState("");
  const [message, setMessage] = useState("");
  const [status, setStatus] = useState({ type: "", text: "" });
  const navigate = useNavigate();
  const recipientEmail = "group.nmc@psn.co.id";

  useEffect(() => {
    if (!localStorage.getItem("email") || !localStorage.getItem("password")) {
      navigate("/");
    }
  }, [navigate]);

  const handleSendEmail = async (e) => {
    e.preventDefault();
    try {
      const email = localStorage.getItem("email");
      const password = localStorage.getItem("password");

      await axios.post("http://192.168.1.13:4000/send-email", {
        email,
        password,
        to: recipientEmail,
        subject,
        message,
      });

      setSubject("");
      setMessage("");
      setStatus({ type: "success", text: "✅ Email sent successfully!" });
      setTimeout(() => setStatus({ type: "", text: "" }), 3000);
    } catch (err) {
      setStatus({ type: "error", text: "❌ Failed to send email. Please try again." });
      console.error("Email sending error:", err);
    }
  };

  return (
    <motion.div 
      className="min-vh-100 vw-100 d-flex justify-content-center align-items-center"
      style={{ background: "linear-gradient(to bottom, #5e5e5e, #1a1a1a)", minHeight: "100vh" }}
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.8, ease: "easeOut" }}
    >
      <motion.div 
        className="p-5 shadow-lg d-flex flex-column align-items-center w-100"
        style={{ borderRadius: "15px", backgroundColor: "#f0f8ff", minHeight: "90vh" }}
        initial={{ opacity: 0, y: 50 }} 
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1, ease: "easeOut" }}
      >
        <motion.img 
          src="/logonmc1.png" 
          alt="Logo" 
          style={{ width: "200px", borderRadius: "10px", marginBottom: "-10px" }}
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1, ease: "easeOut" }}
        />
        <h2 className="text-center" style={{ color: "#2c3e50", fontWeight: "bold" }}>Halo NMC Team!</h2>
        <p className="text-center text-muted">Silakan submit daily activity kamu sebelum pulang</p>

        {status.text && (
          <motion.div 
            className={`alert ${status.type === "success" ? "alert-success" : "alert-danger"} text-center`}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5, ease: "easeOut" }}
          >
            {status.text}
          </motion.div>
        )}

        <form onSubmit={handleSendEmail} className="w-50">
          <div className="mb-3">
            <label className="form-label fw-bold" style={{ color: "#34495e" }}>Recipient Email</label>
            <input type="email" className="form-control border-0 shadow-sm" value={recipientEmail} readOnly />
          </div>
          <div className="mb-3">
            <label className="form-label fw-bold" style={{ color: "#34495e" }}>Subject</label>
            <motion.input
              type="text"
              className="form-control border-0 shadow-sm"
              placeholder="Enter email subject"
              value={subject}
              onChange={(e) => setSubject(e.target.value)}
              required
              style={{ borderRadius: "10px" }}
              whileFocus={{ scale: 1.02 }}
            />
          </div>
          <div className="mb-3">
            <label className="form-label fw-bold" style={{ color: "#34495e" }}>Detail</label>
            <motion.textarea
              className="form-control border-0 shadow-sm"
              placeholder="Input your daily activity"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              required
              rows="5"
              style={{ borderRadius: "10px", minHeight: "200px" }}
              whileFocus={{ scale: 1.02 }}
            ></motion.textarea>
          </div>
          <motion.button 
            type="submit" 
            className="btn w-100" 
            disabled={!subject || !message}
            style={{ backgroundColor: "#3498db", color: "white", fontWeight: "bold", borderRadius: "10px", padding: "10px" }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Submit
          </motion.button>
        </form>

        <motion.button 
          className="btn btn-danger w-50 mt-3" 
          onClick={() => navigate("/logout")}
          style={{ fontWeight: "bold", borderRadius: "10px", padding: "10px" }}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          Logout
        </motion.button>
      </motion.div>
    </motion.div>
  );
}

export default SendEmail;
