import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { motion } from "framer-motion";
import "bootstrap/dist/css/bootstrap.min.css";

function Login() {
  const [emailPrefix, setEmailPrefix] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const emailDomain = "@psn.co.id";
  const fullEmail = emailPrefix ? `${emailPrefix}${emailDomain}` : "";

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    try {
      await axios.post("http://192.168.1.13:4000/login", { email: fullEmail, password });
      localStorage.setItem("email", fullEmail);
      localStorage.setItem("password", password);
      navigate("/Daily-Activity");
    } catch (err) {
      setError("Login failed. Check your credentials.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div 
      className="d-flex flex-column justify-content-center align-items-center"
      style={{
        minHeight: "100vh",
        backgroundColor: "#f0f8ff",  // Warna soft blue
        padding: "0 5%"
      }}
    >
      <motion.img 
        src="/logonmc1.png" 
        alt="Logo NMC" 
        style={{ width: "400px", borderRadius: "10px", marginBottom: "20px" }}
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      />
      <h1 style={{ color: "#2c3e50", fontWeight: "bold", textAlign: "center" }}>Network Monitoring Center</h1>

      <motion.div 
        className="card p-5 shadow-lg mt-4"
        style={{ width: "400px", borderRadius: "20px", backgroundColor: "#ffffff" }}
        initial={{ opacity: 0, y: 50 }} 
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <h2 className="text-center" style={{ color: "#2c3e50", fontWeight: "bold" }}>Welcome Back, NMC Team</h2>
        <p className="text-center text-muted">Please log in to continue</p>
        <form onSubmit={handleLogin}>
          <div className="mb-4">
            <label className="form-label" style={{ fontWeight: "bold", color: "#34495e" }}>Email</label>
            <div className="input-group">
              <input
                type="text"
                className="form-control border-0 shadow-sm"
                placeholder="(e.g., mochamad.dandi)"
                value={emailPrefix}
                onChange={(e) => setEmailPrefix(e.target.value)}
                required
                style={{ borderRadius: "10px 0 0 10px" }}
              />
              <span className="input-group-text border-0 shadow-sm" style={{ backgroundColor: "#ecf0f1", borderRadius: "0 10px 10px 0" }}>{emailDomain}</span>
            </div>
          </div>
          <div className="mb-4">
            <label className="form-label" style={{ fontWeight: "bold", color: "#34495e" }}>Password</label>
            <input
              type="password"
              className="form-control border-0 shadow-sm"
              placeholder="Your Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              style={{ borderRadius: "10px" }}
            />
          </div>
          {error && <p className="text-danger text-center">{error}</p>}
          <motion.button 
            type="submit" 
            className="btn w-100" 
            disabled={loading}
            style={{ backgroundColor: "#3498db", color: "white", fontWeight: "bold", borderRadius: "10px", padding: "10px" }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            {loading ? "Logging in..." : "Login"}
          </motion.button>
          <motion.div 
            className="text-center mt-3"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
          >
            <motion.a 
              href="https://cboss.psn.co.id/" 
              target="_blank" 
              rel="noopener noreferrer" 
              className="btn btn-link text-secondary" 
              style={{ textDecoration: "none", fontWeight: "bold" }}
              whileHover={{ scale: 1.1, color: "#3498db" }}
              whileTap={{ scale: 0.95 }}
            >
              Login to CBOSS
            </motion.a>
          </motion.div>
        </form>
      </motion.div>
    </div>
  );
}

export default Login;
