import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

function Login() {
  const [emailPrefix, setEmailPrefix] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const emailDomain = "@psn.co.id";
  const fullEmail = emailPrefix ? `${emailPrefix}${emailDomain}` : "";

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    try {
      const response = await axios.post("http://192.168.1.13:4000/login", { email: fullEmail, password });
      localStorage.setItem("email", fullEmail);
      localStorage.setItem("password", password);
      navigate("/Daily-Activity");
    } catch (err) {
      setError("Login failed. Check your credentials.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100" style={{ backgroundColor: "#f4f7fc" }}>
      <div className="card p-5 shadow-lg" style={{ width: "400px", borderRadius: "20px", backgroundColor: "#ffffff" }}>
        <div className="text-center mb-3">
          <img src="/logonmc1.png" alt="Logo" style={{ width: "180px", borderRadius: "10px" }} />
        </div>
        <h2 className="text-center" style={{ color: "#2c3e50", fontWeight: "bold" }}>Welcome Back, NMC Team</h2>
        <p className="text-center text-muted">Please log in to continue</p>
        <form onSubmit={handleLogin}>
          <div className="mb-4">
            <label className="form-label" style={{ fontWeight: "bold", color: "#34495e" }}>Email</label>
            <div className="input-group">
              <input
                type="text"
                className="form-control border-0 shadow-sm"
                placeholder="e.g., mochamad.dandi"
                value={emailPrefix}
                onChange={(e) => setEmailPrefix(e.target.value)}
                required
                style={{ borderRadius: "10px 0 0 10px" }}
              />
              <span className="input-group-text border-0 shadow-sm" style={{ backgroundColor: "#ecf0f1", borderRadius: "0 10px 10px 0" }}>{emailDomain}</span>
            </div>
          </div>
          <div className="mb-4">
            <label className="form-label" style={{ fontWeight: "bold", color: "#34495e" }}>Password</label>
            <input
              type="password"
              className="form-control border-0 shadow-sm"
              placeholder="Your Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              style={{ borderRadius: "10px" }}
            />
          </div>
          {error && <p className="text-danger text-center">{error}</p>}
          <button type="submit" className="btn w-100" disabled={loading} style={{ backgroundColor: "#3498db", color: "white", fontWeight: "bold", borderRadius: "10px", padding: "10px" }}>
            {loading ? "Logging in..." : "Login"}
          </button>
          <div className="text-center mt-3">
            <a href="https://cboss.psn.co.id/" target="_blank" rel="noopener noreferrer" className="btn btn-link text-secondary" style={{ textDecoration: "none", fontWeight: "bold" }}>
              Login to CBOSS
            </a>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Login;
