import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { motion } from "framer-motion";
import "bootstrap/dist/css/bootstrap.min.css";

<div 
  className="d-flex justify-content-center align-items-center"
  style={{
    minHeight: "100vh",
    backgroundColor: "#fff", // Changed to white
  }}
>
  <motion.div 
    className="card p-5 shadow-lg"
    style={{ 
      width: "400px", 
      borderRadius: "20px", 
      backgroundColor: "rgba(255, 255, 255, 0.2)",
      backdropFilter: "blur(10px)",
    }}
    initial={{ opacity: 0, y: 50 }} 
    animate={{ opacity: 1, y: 0 }}
    transition={{ duration: 1.5 }}
  >
    <h2 className="text-center" style={{ color: "#fff", fontWeight: "bold" }}>Welcome Back, NMC Team</h2>
    <p className="text-center text-light">Please log in to continue</p>
    <form onSubmit={handleLogin}>
      <div className="mb-4">
        <label className="form-label text-light" style={{ fontWeight: "bold" }}>Email</label>
        <div className="input-group">
          <input
            type="text"
            className="form-control border-0 shadow-sm"
            placeholder="(e.g., mochamad.dandi)"
            value={emailPrefix}
            onChange={(e) => setEmailPrefix(e.target.value)}
            required
            style={{ borderRadius: "10px 0 0 10px" }}
          />
          <span className="input-group-text border-0 shadow-sm" style={{ backgroundColor: "#ecf0f1", borderRadius: "0 10px 10px 0" }}>{emailDomain}</span>
        </div>
      </div>
      <div className="mb-4">
        <label className="form-label text-light" style={{ fontWeight: "bold" }}>Password</label>
        <input
          type="password"
          className="form-control border-0 shadow-sm"
          placeholder="Your Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={{ borderRadius: "10px" }}
        />
      </div>
      {error && <p className="text-danger text-center">{error}</p>}
      <motion.button 
        type="submit" 
        className="btn w-100" 
        disabled={loading}
        style={{ backgroundColor: "#3498db", color: "white", fontWeight: "bold", borderRadius: "10px", padding: "10px" }}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        transition={{ duration: 1.2 }}
      >
        {loading ? "Logging in..." : "Login"}
      </motion.button>
      <motion.div 
        className="text-center mt-3"
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1.5, delay: 0.7 }}
      >
        <motion.a 
          href="https://cboss.psn.co.id/" 
          target="_blank" 
          rel="noopener noreferrer" 
          className="btn btn-link text-light" 
          style={{ textDecoration: "none", fontWeight: "bold" }}
          whileHover={{ scale: 1.1, color: "#3498db" }}
          whileTap={{ scale: 0.95 }}
          transition={{ duration: 1.2 }}
        >
          Login to CBOSS
        </motion.a>
      </motion.div>
    </form>
  </motion.div>
</div>
