import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

function SendEmail() {
  const [subject, setSubject] = useState("");
  const [message, setMessage] = useState("");
  const [status, setStatus] = useState({ type: "", text: "" });
  const navigate = useNavigate();
  const recipientEmail = "group.nmc@psn.co.id"; // Fixed recipient email

  // Redirect ke login jika belum login
  useEffect(() => {
    if (!localStorage.getItem("email") || !localStorage.getItem("password")) {
      navigate("/");
    }
  }, [navigate]);

  const handleSendEmail = async (e) => {
    e.preventDefault();
    try {
      const email = localStorage.getItem("email");
      const password = localStorage.getItem("password");

      await axios.post("http://192.168.1.13:4000/send-email", {
        email,
        password,
        to: recipientEmail,
        subject,
        message,
      });

      // ✅ Clear input fields
      setSubject("");
      setMessage("");

      // ✅ Show success message
      setStatus({ type: "success", text: "✅ Email sent successfully!" });

      // ✅ Hide success message after 3 seconds
      setTimeout(() => setStatus({ type: "", text: "" }), 3000);
    } catch (err) {
      setStatus({ type: "error", text: "❌ Failed to send email. Please try again." });
      console.error("Email sending error:", err);
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <div className="card p-4 shadow-lg" style={{ width: "450px", position: "relative" }}>
        {/* Tombol Logout di pojok kanan atas */}
        <button 
          className="btn btn-danger position-absolute top-0 end-0 m-2" 
          onClick={() => navigate("/logout")}
        >
          &times;
        </button>

        <div className="text-center">
          <img src="logo.png" alt="Logo" style={{ width: "80px" }} />
          <h5 className="mt-3">Hello NMC Team, silakan submit daily activity kamu sebelum pulang</h5>
        </div>

        {/* Pesan Status */}
        {status.text && (
          <div className={`alert ${status.type === "success" ? "alert-success" : "alert-danger"} text-center`}>
            {status.text}
          </div>
        )}

        <form onSubmit={handleSendEmail}>
          <div className="mb-3">
            <label className="form-label">Recipient Email</label>
            <input type="email" className="form-control" value={recipientEmail} readOnly />
          </div>
          <div className="mb-3">
            <label className="form-label">Subject</label>
            <input
              type="text"
              className="form-control"
              placeholder="Enter email subject"
              value={subject}
              onChange={(e) => setSubject(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Detail</label>
            <textarea
              className="form-control"
              placeholder="Silakan input daily mu"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              required
              rows="3"
            ></textarea>
          </div>
          <button type="submit" className="btn btn-success w-100">Submit</button>
        </form>
      </div>
    </div>
  );
}

export default SendEmail;
