import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { motion } from "framer-motion";
import "./Login.css";

function Login() {
  const [emailPrefix, setEmailPrefix] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const emailDomain = "@psn.co.id";
  const fullEmail = emailPrefix ? `${emailPrefix}${emailDomain}` : "";

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    try {
      await axios.post("http://192.168.1.13:4000/login", { email: fullEmail, password });
      localStorage.setItem("email", fullEmail);
      navigate("/Daily-Activity");
    } catch (err) {
      setError("Login failed. Check your credentials.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <motion.div className="login-card" initial={{ opacity: 0, y: -30 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 1.2 }}>
        <div className="login-header">
          <motion.img src="/logonmc1.png" alt="Logo NMC" className="login-logo" initial={{ opacity: 0, scale: 0.8 }} animate={{ opacity: 1, scale: 1 }} transition={{ duration: 1.5 }} />
          <h2>Welcome Back, NMC Team</h2>
          <p>Please login to continue</p>
        </div>

        <form onSubmit={handleLogin} className="login-form">
          <div className="input-group">
            <label>Email</label>
            <div className="input-wrapper">
              <input type="text" placeholder="(e.g., mochamad.dandi)" value={emailPrefix} onChange={(e) => setEmailPrefix(e.target.value)} required />
              <span className="email-domain">{emailDomain}</span>
            </div>
          </div>
          <div className="input-group">
            <label>Password</label>
            <input type="password" placeholder="Your Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          </div>
          {error && <p className="error-message">{error}</p>}
          <motion.button type="submit" className="login-button" disabled={loading} whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
            {loading ? "Logging in..." : "Login"}
          </motion.button>
        </form>

        <div className="cboss-link">
          <motion.a href="https://cboss.psn.co.id/" target="_blank" rel="noopener noreferrer" whileHover={{ scale: 1.1 }}>Login to CBOSS</motion.a>
        </div>
      </motion.div>
    </div>
  );
}

export default Login;
