import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { motion } from "framer-motion";
import "bootstrap/dist/css/bootstrap.min.css";

function Login() {
  const [emailPrefix, setEmailPrefix] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const emailDomain = "@psn.co.id";
  const fullEmail = emailPrefix ? `${emailPrefix}${emailDomain}` : "";

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    try {
      await axios.post("http://192.168.1.13:4000/login", { email: fullEmail, password });
      localStorage.setItem("email", fullEmail);
      localStorage.setItem("password", password);
      navigate("/Daily-Activity");
    } catch (err) {
      setError("Login failed. Check your credentials.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div 
      className="d-flex justify-content-center align-items-center vh-100"
      style={{
        backgroundImage: "url('/gw.jpg')",
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      <motion.div 
        className="card p-4 shadow-lg"
        style={{ width: "350px", borderRadius: "10px", backgroundColor: "rgba(255, 255, 255, 0.2)", backdropFilter: "blur(10px)" }}
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        <h2 className="text-center text-white" style={{ fontWeight: "bold" }}>Login</h2>
        <form onSubmit={handleLogin}>
          <div className="mb-3">
            <input
              type="text"
              className="form-control border-0 shadow-sm"
              placeholder="Username"
              value={emailPrefix}
              onChange={(e) => setEmailPrefix(e.target.value)}
              required
              style={{ borderRadius: "10px" }}
            />
          </div>
          <div className="mb-3">
            <input
              type="password"
              className="form-control border-0 shadow-sm"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              style={{ borderRadius: "10px" }}
            />
          </div>
          {error && <p className="text-danger text-center">{error}</p>}
          <motion.button 
            type="submit" 
            className="btn w-100 text-white" 
            disabled={loading}
            style={{ backgroundColor: "#6A5ACD", fontWeight: "bold", borderRadius: "10px" }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            transition={{ duration: 0.3 }}
          >
            {loading ? "Logging in..." : "Login"}
          </motion.button>
          <div className="text-center mt-3">
            <a href="#" className="text-white" style={{ textDecoration: "none" }}>Forgot password?</a>
          </div>
        </form>
      </motion.div>
    </div>
  );
}

export default Login;
